    private void btnChangeCretendialsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnChangeCretendialsActionPerformed
        try {
            ad = new Admin();
            String userName = txtUserName.getText();
            String password = txtPassword.getText();
            boolean login = ad.validateLogin(userName, password);
            if(login){
                dispose();
                JFrameChangeCretendials chnageCre = new JFrameChangeCretendials();
                chnageCre.setVisible(true);
            }
            else{
                JOptionPane.showMessageDialog(null, "Can't change cretendials, enter correct username and password first.");
                txtPassword.setText("");
                txtUserName.setText("");
            }
        } catch (IOException ex) {
            
        }
    }//GEN-LAST:event_btnChangeCretendialsActionPerformed

